mapper.select:
		        select t.object_id
                   from t_rofh_full_address t
                  where t.cuid = #{id}

select * from ( select rownum n,t.* from ( select FA.LABEL_CN name, FA.object_id,upper(fa.pinyin) pinyin,FA.room_no,t.oldcuid districtid,t.label_cn districtname from t_rofh_full_address FA left join district t on t.cuid = fa.county where 1=1
and replace(fa.label_cn,'|','') like '${detailName}'
and replace(upper(fa.pinyin),'|','') like '%${detailSpell}%'
and t.oldcuid = #{districtId}
and rownum <= #{endIndex} ) t )where n between #{startIndex} and #{endIndex}

      select fa.regiontype1
	   from t_rofh_full_address fa
	  where fa.object_id = #{id}
