mapper.select:
			select  (aw.port_num - aw.used_port_num) num
                    from an_wbs aw
         	where aw.cuid = #{id}

   			select count(1) num,decode(min(n.ownership),2,'CTT','FTTH') type from an_pos ap,ptp p,nms_system n
               where  ap.related_cab_cuid = #{FIBER_CAB_CUID}
                   and ap.live_cycle = 1
            	   and p.related_ne_cuid = ap.cuid
            	   and p.port_state = 1
                   and p.port_sub_type = 13
                   and p.dev_type = 3
                   and n.cuid = ap.related_ems_cuid
                   
						and ap.cuid = #{RELATED_POS_CUID}
				    
				   
				   	    and p.port_no  >=  #{START_PORT_NUM}
				   
				   
				   	    and p.port_no  <=  #{END_PORT_NUM}
